import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AddForm = ({ fetchUsers, isEditMode, editedUser, setEditMode }) => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    contact: '',
    address: '',
    pincode: ''
  });

  useEffect(() => {
    if (isEditMode) {
      setFormData({
        firstName: editedUser.firstName || '',
        lastName: editedUser.lastName || '',
        email: editedUser.email || '',
        contact: editedUser.contact || '',
        address: editedUser.address || '',
        pincode: editedUser.pincode || ''
      });
    } else {
      setFormData({
        firstName: '',
        lastName: '',
        email: '',
        contact: '',
        address: '',
        pincode: ''
      });
    }
  }, [isEditMode, editedUser]);

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      if (isEditMode) {
        await axios.put(`http://localhost:5000/api/users/${editedUser._id}`, formData);
        alert('User updated');
      } else {
        await axios.post('http://localhost:5000/api/users', formData);
        alert('User added');
      }
      fetchUsers();
      setEditMode(false);
    } catch (error) {
      console.error('Failed to submit form: ', error);
      alert('Failed to submit form');
    }
  };

  return (
    <div className="input-container">
      {isEditMode && <h2>Edit User</h2>}
      <form onSubmit={handleSubmit}>
        {isEditMode && (
          <>
            <div>
              <label>First Name:</label>
              <input type="text" name="firstName" value={formData.firstName} onChange={handleChange} />
            </div>
            <div>
              <label>Last Name:</label>
              <input type="text" name="lastName" value={formData.lastName} onChange={handleChange} />
            </div>
            <div>
              <label>Email:</label>
              <input type="email" name="email" value={formData.email} onChange={handleChange} />
            </div>
            <div>
              <label>Contact:</label>
              <input type="text" name="contact" value={formData.contact} onChange={handleChange} />
            </div>
            <div>
              <label>Address:</label>
              <input type="text" name="address" value={formData.address} onChange={handleChange} />
            </div>
            <div>
              <label>Pincode:</label>
              <input type="text" name="pincode" value={formData.pincode} onChange={handleChange} />
            </div>
            <button type="submit">Update</button>
          </>
        )}
      </form>
    </div>
  );
};

export default AddForm;
