import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AddForm = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    contact: '',
    address: '',
    pincode: ''
  });
  const [isEditMode, setIsEditMode] = useState(false);
  const [editedUserId, setEditedUserId] = useState('');

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      if (isEditMode) {
        // If in edit mode, send PUT request to update user
        await axios.put(`http://localhost:5000/api/users/${editedUserId}`, formData);
        alert('User Updated');
      } else {
        // If not in edit mode, send POST request to add new user
        await axios.post('http://localhost:5000/api/users', formData);
        alert('User Added');
      }
      setFormData({
        firstName: '',
        lastName: '',
        email: '',
        contact: '',
        address: '',
        pincode: ''
      });
      setIsEditMode(false); // Reset edit mode after submission
    } catch (error) {
      console.error('Failed to Add/Update User: ', error);
      alert('Failed to Add/Update User');
    }
  };

  const handleEdit = (user) => {
    // Set form data with user's information
    setFormData({
      firstName: user.firstName,
      lastName: user.lastName,
      email: user.email,
      contact: user.contact,
      address: user.address,
      pincode: user.pincode
    });
    setIsEditMode(true); // Set edit mode to true
    setEditedUserId(user._id); // Set the ID of the user being edited
  };

  return (
    <div>
      <h2>{isEditMode ? 'Edit User' : 'Add New User'}</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>First Name:</label>
          <input type="text" name="firstName" value={formData.firstName} placeholder="Enter First Name" onChange={handleChange} required />
        </div>
        {/* Other input fields... */}
        <button type="submit">{isEditMode ? 'Update' : 'Submit'}</button>
      </form>

      {/* User List */}
      <h2>User List</h2>
      <table>
        <thead>
          <tr>
            <th>First Name</th>
            {/* Other table headers... */}
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {users.map(user => (
            <tr key={user._id}>
              <td>{user.firstName}</td>
              {/* Other table data... */}
              <td>
                <button onClick={() => handleEdit(user)}>Edit</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AddForm;
