import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [isEditMode, setIsEditMode] = useState(false);
  const [editData, setEditData] = useState({});
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    contact: '',
    address: '',
    pincode: ''
  });

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/users');
      setUsers(response.data);
    } catch (error) {
      console.error('Failed to fetch users: ', error);
    }
  };

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleEditClick = (userData) => {
    setIsEditMode(true);
    setEditData(userData);
    setFormData(userData); 
    debugger
  };

  const updateUser = async () => {
    try {
      await axios.put(`http://localhost:5000/api/users/${editData._id}`, formData); 
      alert('User data updated ');
      setIsEditMode(false);
      setFormData({
        firstName: '',
        lastName: '',
        email: '',
        contact: '',
        address: '',
        pincode: ''
      });
      fetchUsers();
    } catch (error) {
      console.error('Failed to update user: ', error);
      alert('Failed to update user');
    }
  };

  const deleteUser = async (userId) => {
    try {
      await axios.delete(`http://localhost:5000/api/users/${userId}`);
      alert('User deleted');
      fetchUsers();
    } catch (error) {
      console.error('Failed to delete user: ', error);
      alert('Failed to delete user');
    }
  };

  return (
    <div>
      <div className="table-wrapper">
        <h2>User List</h2>
        <table>
          <thead>
            <tr>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Email</th>
              <th>Contact</th>
              <th>Address</th>
              <th>Pincode</th>
              <th>Action</th>
              <th>Edit</th>
            </tr>
          </thead>
          <tbody>
            {users.map(user => (
              <tr key={user._id}>
                <td>{user.firstName}</td>
                <td>{user.lastName}</td>
                <td>{user.email}</td>
                <td>{user.contact}</td>
                <td>{user.address}</td>
                <td>{user.pincode}</td>
                <td>
                  <button onClick={() => deleteUser(user._id)} className="delete-button">
                    Delete
                  </button>
                </td>
                <td>
                  <button onClick={() => handleEditClick(user)} className="edit-button">
                    Edit
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      {isEditMode && (
        <div>
          <h2>Edit User</h2>
          <form onSubmit={updateUser}>
            <div>
              <label>First Name:</label>
              <input type="text" name="firstName" value={formData.firstName} onChange={handleChange} />
            </div>
            <div>
              <label>Last Name:</label>
              <input type="text" name="lastName" value={formData.lastName} onChange={handleChange} />
            </div>
            <div>
              <label>Email:</label>
              <input type="email" name="email" value={formData.email} onChange={handleChange} />
            </div>
            <div>
              <label>Contact:</label>
              <input type="text" name="contact" value={formData.contact} onChange={handleChange} />
            </div>
            <div>
              <label>Address:</label>
              <input type="text" name="address" value={formData.address} onChange={handleChange} />
            </div>
            <div>
              <label>Pincode:</label>
              <input type="text" name="pincode" value={formData.pincode} onChange={handleChange} />
            </div>
            <button type="submit">
              {isEditMode ? 'Update' : 'Add New User'}
            </button>
          </form>
        </div>
      )}
    </div>
  );
};

export default UserList;
