import React, { useState } from 'react';
import axios from 'axios';
import { fetchUsers } from './UserList'; // Import the fetchUsers function

// Component definition for AddForm
const AddForm = ({ fetchUsers }) => {
  const [formData, setFormData] = useState({
    lastName: '',
    email: '',
    contact: '',
    address: '',
    pincode: ''
  });

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/users', formData);
      setFormData({
        lastName: '',
        email: '',
        contact: '',
        address: '',
        pincode: ''
      });
      alert('User Added');
      fetchUsers(); // Fetch updated users data after adding a new user
    } catch (error) {
      console.error('Failed to Add User: ', error);
      alert('Failed to Add user');
    }
  };

  return (
    <div>
      {/* No form section is rendered */}
    </div>
  );
};

// Export statement
export default AddForm;
