import React, { useState, useEffect } from 'react';
import axios from 'axios';
import AddForm from './AddForm';

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [isEditMode, setIsEditMode] = useState(false);
  const [editedUser, setEditedUser] = useState(null);

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/users');
      setUsers(response.data);
    } catch (error) {
      console.error('Failed to fetch users: ', error);
    }
  };

  const handleEditClick = (user) => {
    setIsEditMode(true);
    setEditedUser(user);
  };

  return (
    <div>
      <h2>User List</h2>
      <table>
        <thead>
          <tr>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Email</th>
            <th>Contact</th>
            <th>Address</th>
            <th>Pincode</th>
            <th>Action</th>
            <th>Edit</th>
          </tr>
        </thead>
        <tbody>
          {users.map(user => (
            <tr key={user._id}>
              <td>{user.firstName}</td>
              <td>{user.lastName}</td>
              <td>{user.email}</td>
              <td>{user.contact}</td>
              <td>{user.address}</td>
              <td>{user.pincode}</td>
              <td>
                <button onClick={() => deleteUser(user._id)} className="delete-button">
                  Delete
                </button>
              </td>
              <td>
                <button onClick={() => handleEditClick(user)} className="edit-button">
                  Edit
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <AddForm fetchUsers={fetchUsers} isEditMode={isEditMode} editedUser={editedUser} setEditMode={setIsEditMode} />
    </div>
  );
};

export default UserList;
