import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { fetchUsers } from './UserList'; // Import fetchUsers function from UserList

const AddForm = ({ editData }) => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    contact: '',
    address: '',
    pincode: ''
  });

  useEffect(() => {
    if (editData) {
      setFormData(editData); // Set form data for editing if editData is provided
    }
  }, [editData]);

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      if (editData) {
        await axios.put(`http://localhost:5000/api/users/${editData._id}`, formData);
        alert('User data updated');
      } else {
        await axios.post('http://localhost:5000/api/users', formData);
        alert('User added');
      }
      setFormData({
        firstName: '',
        lastName: '',
        email: '',
        contact: '',
        address: '',
        pincode: ''
      });
      fetchUsers(); // Fetch users again after adding or updating a user
    } catch (error) {
      console.error('Failed to submit form: ', error);
      alert('Failed to submit form');
    }
  };

  return (
    <div className="input-container">
      <h2>{editData ? 'Edit User' : 'Add New User'}</h2>
      <form onSubmit={handleSubmit}>
        {/* Form inputs */}
        <button type="submit" className="submit-button">{editData ? 'Update' : 'Submit'}</button>
      </form>
    </div>
  );
};

export default AddForm;
