import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AddForm = ({ initialData, onSubmit }) => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    contact: '',
    address: '',
    pincode: ''
  });

  useEffect(() => {
    if (initialData) {
      setFormData(initialData);
    }
  }, [initialData]);

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      await onSubmit(formData);
      setFormData({
        firstName: '',
        lastName: '',
        email: '',
        contact: '',
        address: '',
        pincode: ''
      });
      alert(initialData ? 'User Updated' : 'User Added');
    } catch (error) {
      console.error(`Failed to ${initialData ? 'update' : 'add'} user: `, error);
      alert(`Failed to ${initialData ? 'update' : 'add'} user`);
    }
  };

  return (
    <div>
      <h2>{initialData ? 'Edit User' : 'Add New User'}</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>First Name:</label>
          <input type="text" name="firstName" value={formData.firstName} placeholder="Enter First Name" onChange={handleChange} required />
        </div>
        <div>
          <label>Last Name:</label>
          <input type="text" name="lastName" value={formData.lastName} placeholder="Enter Last Name" onChange={handleChange} required />
        </div>
        <div>
          <label>Email:</label>
          <input type="email" name="email" value={formData.email} placeholder="Enter Email" onChange={handleChange} required />
        </div>
        <div>
          <label>Contact:</label>
          <input type="text" name="contact" value={formData.contact} placeholder="Enter Contact No. " onChange={handleChange} required />
        </div>
        <div>
          <label>Address:</label>
          <input type="text" name="address" value={formData.address} placeholder="Enter Address" onChange={handleChange} required />
        </div>
        <div>
          <label>Pincode:</label>
          <input type="text" name="pincode" value={formData.pincode} placeholder="Pincode" onChange={handleChange} required />
        </div>
        <button type="submit">{initialData ? 'Update' : 'Submit'}</button>
      </form>
    </div>
  );
};

export default AddForm;
