import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';

import AddForm from './components/AddForm';
import UserList from './components/UserList';
import Login from './auth/Login';
import Signup from './auth/Signup';
import PrivateRoute from './auth/PrivateRoute';

const App = () => {
  const [users, setUsers] = useState([]);
  const [editData, setEditData] = useState(null);

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/users');
      setUsers(response.data);
    } catch (error) {
      console.error('Failed to fetch users: ', error);
    }
  };

  const editUser = (userData) => {
    setEditData(userData); // Set the edit data
  };

  return (
    <Router>
        <Routes></Routes>
        <PrivateRoute exact path="/" render={(props) => <AddForm {...props} fetchUsers={fetchUsers} editData={editData} />} />
        <PrivateRoute exact path="/users" render={(props) => <UserList {...props} users={users} editUser={editUser} />} />
        <Route path="/login" component={Login} />
        <Route path="/signup" component={Signup} />
      
    </Router>
  );
};

export default App;
