import React, { useState, useEffect } from 'react';
import axios from 'axios';
import AddForm from './AddForm'; // Import the AddForm component

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [editingUser, setEditingUser] = useState(null); // State to hold the user being edited

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/users');
      setUsers(response.data);
    } catch (error) {
      console.error('Failed to fetch users: ', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/users/${id}`);
      fetchUsers(); // Fetch users again after deletion
      alert('User deleted successfully');
    } catch (error) {
      console.error('Failed to delete user: ', error);
      alert('Failed to delete user');
    }
  };

  const handleEdit = (user) => {
    // Populate the AddForm with the data of the user being edited
    setEditingUser(user);
  };

  const handleUpdate = async (updatedUserData) => {
    try {
      await axios.put(`http://localhost:5000/api/users/${editingUser._id}`, updatedUserData);
      fetchUsers(); // Fetch users again after updating
      setEditingUser(null); // Reset editingUser state
      alert('User updated successfully');
    } catch (error) {
      console.error('Failed to update user: ', error);
      alert('Failed to update user');
    }
  };

  return (
    <div>
      <h2>User List</h2>
      <table>
        <thead>
          <tr>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Email</th>
            <th>Contact</th>
            <th>Address</th>
            <th>Pincode</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {Array.isArray(users) && users.map(user => (
            <tr key={user._id}>
              <td>{user.firstName}</td>
              <td>{user.lastName}</td>
              <td>{user.email}</td>
              <td>{user.contact}</td>
              <td>{user.address}</td>
              <td>{user.pincode}</td>
              <td>
                <button className="edit-button" onClick={() => handleEdit(user)}>Edit</button>
                <button className="delete-button" onClick={() => handleDelete(user._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* AddForm for editing */}
      {editingUser && (
        <div>
          <h2>Edit User</h2>
          <AddForm initialData={editingUser} onSubmit={handleUpdate} />
        </div>
      )}
    </div>
  );
};

export default UserList;
