import React from 'react';
import AddForm from './AddForm';
import UserList from './UserList';

const App = () => {
  const fetchUsers = async () => {
    try {
      // Fetch users data from the server
      const response = await axios.get('http://localhost:5000/api/users');
      setUsers(response.data);
    } catch (error) {
      console.error('Failed to fetch users: ', error);
    }
  };

  const deleteUser = async (userId) => {
    try {
      // Delete user with the given userId
      await axios.delete(`http://localhost:5000/api/users/${userId}`);
      // Fetch updated users data after deletion
      fetchUsers();
      alert('User deleted successfully');
    } catch (error) {
      console.error('Failed to delete user: ', error);
      alert('Failed to delete user');
    }
  };

  const editUser = async (userId, userData) => {
    try {
      // Set the edited user data to the form state for editing
      setFormData(userData);
      setIsEditMode(true);
    } catch (error) {
      console.error('Failed to edit user: ', error);
      alert('Failed to edit user');
    }
  };

  return (
    <div>
      <AddForm fetchUsers={fetchUsers} />
      <UserList fetchUsers={fetchUsers} deleteUser={deleteUser} editUser={editUser} />
    </div>
  );
};

export default App;
