import React, { useState } from 'react';
import axios from 'axios';
import UserList from './UserList'; // Importing UserList component

const AddForm = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    contact: '',
    address: '',
    pincode: ''
  });

  const [isEditMode, setIsEditMode] = useState(false); // State for edit mode
  const [editedUserData, setEditedUserData] = useState(null); // State to store edited user data

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/users', formData);
      setFormData({
        firstName: '',
        lastName: '',
        email: '',
        contact: '',
        address: '',
        pincode: ''
      });
      alert('User Added');
    } catch (error) {
      console.error('Failed to Add User: ', error);
      alert('Failed to Add user');
    }
  };

  const handleEdit = (userData) => {
    setIsEditMode(true); // Set edit mode to true
    setEditedUserData(userData); // Set edited user data
    setFormData(userData); // Populate form data with edited user data
  };

  return (
    <div>
      <h2>{isEditMode ? 'Edit User' : 'Add New User'}</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>First Name:</label>
          <input type="text" name="firstName" value={formData.firstName} placeholder="Enter First Name" onChange={handleChange} required />
        </div>
        <div>
          <label>Last Name:</label>
          <input type="text" name="lastName" value={formData.lastName} placeholder="Enter Last Name" onChange={handleChange} required />
        </div>
        <div>
          <label>Email:</label>
          <input type="email" name="email" value={formData.email} placeholder="Enter Email" onChange={handleChange} required />
        </div>
        <div>
          <label>Contact:</label>
          <input type="text" name="contact" value={formData.contact} placeholder="Enter Contact No. " onChange={handleChange} required />
        </div>
        <div>
          <label>Address:</label>
          <input type="text" name="address" value={formData.address} placeholder="Enter Address" onChange={handleChange} required />
        </div>
        <div>
          <label>Pincode:</label>
          <input type="text" name="pincode" value={formData.pincode} placeholder="Pincode" onChange={handleChange} required />
        </div>
        <button type="submit">{isEditMode ? 'Update' : 'Submit'}</button>
      </form>
      {isEditMode && editedUserData && ( // Render UserList only in edit mode and when editedUserData is available
        <div>
          <h2>User List</h2>
          <UserList handleEdit={handleEdit} />
        </div>
      )}
    </div>
  );
};

export default AddForm;
