import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [isEditMode, setIsEditMode] = useState(false);
  const [editedUserId, setEditedUserId] = useState('');
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    contact: '',
    address: '',
    pincode: ''
  });

  // Define fetchUsers function
  const fetchUsers = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/users');
      setUsers(response.data);
    } catch (error) {
      console.error('Failed to fetch users: ', error);
    }
  };

  // Call fetchUsers on component mount
  useEffect(() => {
    fetchUsers();
  }, []);

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const editUser = (userId, userData) => {
    setIsEditMode(true);
    setEditedUserId(userId);
    setFormData(userData);
  };

  // Update user function
  const updateUser = async () => {
    try {
      await axios.put(`http://localhost:5000/api/users/${editedUserId}`, formData);
      alert('User data updated ');
      setIsEditMode(false);
      setFormData({
        firstName: '',
        lastName: '',
        email: '',
        contact: '',
        address: '',
        pincode: ''
      });
      fetchUsers(); // Fetch updated user data after updating
    } catch (error) {
      console.error('Failed to update user: ', error);
      alert('Failed to update user');
    }
  };

  // JSX
  return (
    <div>
      <div className="table-wrapper">
        <h2>User List</h2>
      </div>
      <table>
        {/* Table content */}
      </table>
      {/* Edit form */}
      {isEditMode && (
        <div>
          <h2>Edit User</h2>
          <form onSubmit={updateUser}>
            {/* Form fields */}
          </form>
        </div>
      )}
    </div>
  );
};

export default UserList;
