import React, { useState } from 'react';
import axios from 'axios';

const AddForm = ({ editMode, userData }) => {
  const [formData, setFormData] = useState({
    firstName: userData ? userData.firstName : '',
    lastName: userData ? userData.lastName : '',
    email: userData ? userData.email : '',
    contact: userData ? userData.contact : '',
    address: userData ? userData.address : '',
    pincode: userData ? userData.pincode : ''
  });

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      if (editMode) {
        await axios.put(`http://localhost:5000/api/users/${userData._id}`, formData);
        alert('User data updated');
      } else {
        await axios.post('http://localhost:5000/api/users', formData);
        alert('User added');
      }
      setFormData({
        firstName: '',
        lastName: '',
        email: '',
        contact: '',
        address: '',
        pincode: ''
      });
    } catch (error) {
      console.error('Failed to add/update user: ', error);
      alert('Failed to add/update user');
    }
  };

  return (
    <div className="input-container">
      <h2>{editMode ? 'Edit User' : 'Add New User'}</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>First Name:</label>
          <input type="text" name="firstName" value={formData.firstName} placeholder="Enter First Name" onChange={handleChange} required />
        </div>
        <div>
          <label>Last Name:</label>
          <input type="text" name="lastName" value={formData.lastName} placeholder="Enter Last Name" onChange={handleChange} required />
        </div>
        <div>
          <label>Email:</label>
          <input type="email" name="email" value={formData.email} placeholder="Enter Email" onChange={handleChange} required />
        </div>
        <div>
          <label>Contact:</label>
          <input type="text" name="contact" value={formData.contact} placeholder="Enter Contact No. " onChange={handleChange} required />
        </div>
        <div>
          <label>Address:</label>
          <input type="text" name="address" value={formData.address} placeholder="Enter Address" onChange={handleChange} required />
        </div>
        <div>
          <label>Pincode:</label>
          <input type="text" name="pincode" value={formData.pincode} placeholder="Pincode" onChange={handleChange} required />
        </div>
        <button type="submit">{editMode ? 'Update' : 'Submit'}</button>
      </form>
    </div>
  );
};

export default AddForm;
